import numpy as npimport matplotlibmatplotlib.use("TkAgg")import matplotlib.pyplot as plt# --- 1. Початкові координати квадрата (один обхід по периметру) ---# Розширена матриця координат (x, y, 1)my_square = np.array([    [0, 0, 1],    [2, 0, 1],    [2, 2, 1],    [0, 2, 1],    [0, 0, 1]   # закриваємо контур]).T  # транспонуємо для роботи з матрицями (3 x N)# --- 2. Матриця зсуву ---tx, ty = 3, 1T = np.array([    [1, 0, tx],    [0, 1, ty],    [0, 0, 1]])# --- 3. Застосування перетворення ---square_translated = T @ my_square# --- 4. Візуалізація ---plt.figure(figsize=(6, 6))plt.plot(my_square[0, :], my_square[1, :], 'b-o', label="Початковий квадрат")plt.plot(square_translated[0, :], square_translated[1, :], 'r-o', label="Зсунуте зображення")plt.axhline(0, color='gray', linewidth=0.5)plt.axvline(0, color='gray', linewidth=0.5)plt.legend()plt.gca().set_aspect('equal', adjustable='box')plt.title("Зсув квадрата (Translation)")plt.show()