import cv2left = cv2.imread("left.png")right = cv2.imread("right.png")if left is None or right is None:    print("Помилка: переконайтесь, що файли left.jpg та right.jpg існують!")    exit()h, w = left.shape[:2]right = cv2.resize(right, (w, h))# Канал R беремо з лівого зображенняr = left[:, :, 2]# Канали G та B з правогоg = right[:, :, 1]b = right[:, :, 0]# Об’єднання у новий кадр BGRanaglyph = cv2.merge((b, g, r))cv2.imwrite("anaglyph.png", anaglyph)cv2.imshow("Anaglyph 3D", anaglyph)print("Анагліф створено! Файл збережений як anaglyph.png")cv2.waitKey(0)cv2.destroyAllWindows()